mapper.update:
UPDATE user_info
SET refresh_token = #{refreshToken}
WHERE login_id = #{loginId}

UPDATE user_info
   SET 
   update_date = date_format(now(),'%Y-%m-%d %H:%i:%s')
   ,update_id = #{updateId}

   ,login_id = #{loginId}


   ,user_password = #{userPassword}


   ,user_name = #{userName}


   ,email = #{email}


   ,user_phone_num = #{userPhoneNum}


   ,use_status = #{useStatus}


   ,roll_id = #{rollId}

   WHERE 
   	 user_id = #{userId}

	UPDATE user_info
	   SET use_status = "N"
	 WHERE 


		user_id in 

            #{item}



		user_id = #{userId}

mapper.select:
SELECT u.user_id, u.login_id, u.user_password, u.user_name, u.email, u.user_phone_num, u.use_status, u.delete_status, u.roll_id, u.create_id, u.create_date, u.update_id, u.update_date, r.roll_name FROM user_info AS u LEFT OUTER JOIN roll_code_info AS r ON u.roll_id = r.roll_id WHERE use_status = 'Y' AND 1 = 1
and u.user_name like concat('%', #{userName}, '%')
order by user_id desc limit #{startNum}, #{rowsPerPage}
